import React, { useState } from 'react';
import './Startgame.css';

const Startgame = () => {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [isXTurn, setIsXTurn] = useState(true);
  const [winner, setWinner] = useState(null);
  const [displayPage, setDisplayPage] = useState('sectiongame');

  const winPatterns = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  const checkWinner = (newBoard) => {
    for (let pattern of winPatterns) {
      const [a, b, c] = pattern;
      if (newBoard[a] && newBoard[a] === newBoard[b] && newBoard[a] === newBoard[c]) {
        return newBoard[a];
      }
    }
    return null;
  };

  const checkDraw = (newBoard) => {
    return newBoard.every((cell) => cell !== null);
  };

  const handleBoxClick = (index) => {
    if (board[index] || winner) return;
    const newBoard = board.slice();
    newBoard[index] = isXTurn ? 'X' : 'O';
    setBoard(newBoard);
    const gameWinner = checkWinner(newBoard);
    if (gameWinner) {
      setWinner(gameWinner);
      setDisplayPage('winningpage');
    } else if (checkDraw(newBoard)) {
      setWinner('Draw');
      setDisplayPage('winningpage');
    } else {
      setIsXTurn(!isXTurn);
    }
  };

  const handleRestart = () => {
    setBoard(Array(9).fill(null));
    setIsXTurn(true);
    setWinner(null);
    setDisplayPage('sectiongame');
  };

  const renderBox = (index) => {
    return (
      <button className="box" onClick={() => handleBoxClick(index)}>
        {board[index]}
      </button>
    );
  };

  const renderGame = () => (
    <div className='game-container'>
      <div className='topheading'>
        <h1>X <span>O</span></h1>
        <p>{isXTurn ? "X's TURN" : "O's TURN"}</p>
        <button onClick={handleRestart}>
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="" className="bi bi-repeat" viewBox="0 0 16 16">
            <path d="M11 5.466V4H5a4 4 0 0 0-3.584 5.777.5.5 0 1 1-.896.446A5 5 0 0 1 5 3h6V1.534a.25.25 0 0 1 .41-.192l2.36 1.966c.12.1.12.284 0 .384l-2.36 1.966a.25.25 0 0 1-.41-.192m3.81.086a.5.5 0 0 1 .67.225A5 5 0 0 1 11 13H5v1.466a.25.25 0 0 1-.41.192l-2.36-1.966a.25.25 0 0 1 0-.384l2.36-1.966a.25.25 0 0 1 .41.192V12h6a4 4 0 0 0 3.585-5.777.5.5 0 0 1 .225-.67Z" />
          </svg>
        </button>
      </div>
      <div className='boxes'>
        {board.map((_, index) => renderBox(index))}
      </div>
    </div>
  );

  const renderWinningPage = () => (
    <div className='winpage'>
      <h1>{winner === 'Draw' ? "It's a Draw!" : `Congratulations ${winner}! You win!`}</h1>
      <button className="start" onClick={handleRestart}>Play Again</button>
    </div>
  );

  return (
    <div className='game'>
      {displayPage === 'sectiongame' ? renderGame() : renderWinningPage()}
    </div>
  );
};

export default Startgame;
