import React, { useState } from 'react';
import './Gamebox.css';

function Gamebox({ onStartGame }) {
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  const human = () => {
    alert("Coming soon");
  };

  const invite = () => {
    alert("Invite your friend");
  };

  const startGame = () => {
    if (selectedPlayer) {
      onStartGame(selectedPlayer);
    } else {
      alert("Please select a player");
    }
  };

  return (
    <div className='game'>
      <div className='game-container'>
        <p id="X">X <span id='O'>O</span></p>
        <div className='pickPlayer'>
          PICK PLAYER<br />
          <div>
            <button 
              className={`button1 ${selectedPlayer === 'X' ? 'selected' : ''}`} 
              onClick={() => setSelectedPlayer('X')}
            >
              X
            </button>
            <button 
              className={`button2 ${selectedPlayer === 'O' ? 'selected' : ''}`} 
              onClick={() => setSelectedPlayer('O')}
            >
              O
            </button>
          </div>
        </div>
        <button className='cpu' onClick={startGame}>NEW GAME(VS CPU)</button>
        <button className='human' onClick={human}>NEW GAME(VS HUMAN) <span>Coming soon</span></button>
        <button className='invite' onClick={invite}>Invite your friend</button>
      </div>
    </div>
  );
}

export default Gamebox;
