import React, { useState, useEffect } from 'react';
import './Startgame.css';

const Startgame = ({ selectedPlayer }) => {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [isXTurn, setIsXTurn] = useState(selectedPlayer === 'X');
  const [winner, setWinner] = useState(null);
  const [ties, setTies] = useState(0);
  const [xWins, setXWins] = useState(0);
  const [oWins, setOWins] = useState(0);

  useEffect(() => {
    if (winner) {
      if (winner === 'X') {
        setXWins(xWins + 1);
      } else if (winner === 'O') {
        setOWins(oWins + 1);
      }
      return;
    }

    if (!isXTurn && !winner) {
      const emptyIndices = board.map((val, index) => val === null ? index : null).filter(val => val !== null);
      if (emptyIndices.length > 0) {
        const randomIndex = emptyIndices[Math.floor(Math.random() * emptyIndices.length)];
        const newBoard = board.slice();
        newBoard[randomIndex] = selectedPlayer === 'X' ? 'O' : 'X';
        setBoard(newBoard);
        setIsXTurn(true);
        checkWinner(newBoard);
      }
    }
  }, [isXTurn, board, winner, selectedPlayer]);

  const handleClick = (index) => {
    if (board[index] || winner) return;
    const newBoard = board.slice();
    newBoard[index] = selectedPlayer;
    setBoard(newBoard);
    setIsXTurn(false);
    checkWinner(newBoard);
  };

  const checkWinner = (newBoard) => {
    const winningCombinations = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];

    for (let combination of winningCombinations) {
      const [a, b, c] = combination;
      if (newBoard[a] && newBoard[a] === newBoard[b] && newBoard[a] === newBoard[c]) {
        setWinner(newBoard[a]);
        return;
      }
    }

    if (newBoard.every(cell => cell !== null)) {
      setTies(ties + 1);
      setWinner('Tie');
    }
  };

  const handleReset = () => {
    setBoard(Array(9).fill(null));
    setIsXTurn(selectedPlayer === 'X');
    setWinner(null);
  };

  return (
    <div className='game'>
      <div className='game-container'>
        <div className='topheading'>
          <h1>X <span>O</span></h1>
          <p>{isXTurn ? "X's TURN" : "O's TURN"}</p>
          <button onClick={handleReset}>
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="" className="bi bi-repeat" viewBox="0 0 16 16">
              <path d="M11 5.466V4H5a4 4 0 0 0-3.584 5.777.5.5 0 1 1-.896.446A5 5 0 0 1 5 3h6V1.534a.25.25 0 0 1 .41-.192l2.36 1.966c.12.1.12.284 0 .384l-2.36 1.966a.25.25 0 0 1-.41-.192m3.81.086a.5.5 0 0 1 .67.225A5 5 0 0 1 11 13H5v1.466a.25.25 0 0 1-.41.192l-2.36-1.966a.25.25 0 0 1 0-.384l2.36-1.966a.25.25 0 0 1 .41.192V12h6a4 4 0 0 0 3.585-5.777.5.5 0 0 1 .225-.67Z" />
            </svg>
          </button>
        </div>
        <div className='boxes'>
          {board.map((cell, index) => (
            <button
              key={index}
              className={`box ${cell === 'X' ? 'x' : cell === 'O' ? 'o' : ''}`}
              onClick={() => handleClick(index)}
            >
              {cell}
            </button>
          ))}
        </div>
        <div className='bottom'>
          <p className='p1'>X(YOU)<br />{xWins}</p>
          <p className='p2'>TIES<br />{ties}</p>
          <p className='p3'>O(CPU)<br />{oWins}</p>
        </div>
      </div>
    </div>
  );
};

export default Startgame;
